import { Component } from "react";


class MovieCard extends Component {
  movie = {};

  constructor(props) {
    super(props);
    this.movie = props.movie;
    this.connection = props.lain;
  }

  render() {
    return (
      <div className="col-lg-3 col-md-4 col-sm-6 my-3">
        {this.connection}
        <div className="card h-100">
          <img
            src={this.movie.image_url}
            className="card-img-top"
            alt="skilvul"
          />
          <div className="card-body">
            {/* <h5 className="card-title">{ this.name }</h5> */}
            <h5 className="card-title">{this.movie.title}</h5>
            <h6 className="card-subtitle mb-2">
              <span
                className={`badge ${
                  this.movie.type === "TV" ? "bg-danger" : "bg-success"
                }`}
              >
                {this.movie.type}
              </span>
            </h6>
            <p className="card-text">
              Some quick example text to build on the card title and make up the
              bulk of the card's content.
            </p>
          </div>
          <div className="card-body">
            <a
              href={this.movie.url}
              target="_blank"
              className="btn btn-primary w-100 text-uppercase"
            >
              readmore
            </a>
          </div>
        </div>
      </div>
    );
  }
}

export default MovieCard;
